import React, { Component } from 'react';
import './App.css';
import PicCard from "./PicCard";
import img from "./img.json";

class App extends Component {
  state = {
    img,
    count: 0
  };

  // increase (is this count as variable?) = {
  //   count: 0
  // }; 

  imageClick = () => {
    this.setState({
      count: this.state.count + 1
    });
  };

  render() {
    return (
  <div className="p-3 mb-2 bg-light text-dark">
  <div className="row p-3 mb-2 bg-info text-white text-center">
    <div className="col">
      <h4>Clicky Game</h4>
    </div>
    <div className="col">
      <h4>Click an image to begin!</h4>
    </div>
    <div className="col">
      <h4>Score: {this.state.count} | Top Score: {this.state.count}</h4>
    </div>
  </div>
  <h1 className="text-center">Clicky Game!</h1>
  <h2 className="text-center">Click on an image to earn points, but don't click on any more than once!</h2>
  <div className="container">
  {this.state.img.map(imgs => (
        <PicCard
          imageClick={this.imageClick}
          id={imgs.id}
          key={imgs.id}
          image={imgs.image}
          count={this.state.count}
          />
      ))}
  </div>

  </div>
    );
  }
}

export default App;
