import React, { Component } from 'react';
import './App.css';
import PicCard from "./PicCard";
import img from "./img.json";

class App extends Component {
  state = {
    img,
    count: 0,
    count1: 0
  };

  // increase (is this count as variable?) = {
  //   count: 0
  // }; 


  imageClick = () => {
    // this.setState({
    //   count: this.state.count + 1,
    //   count1: this.state.count1 + 3
    // });
    function shuffle(img) {
      var i, j, temp;
      for (i = img.length - 1; i > 0; i--) {
        j = Math.floor(Math.random() * (i + 1));
        temp = img[i];
        img[i] = img[j];
        img[j] = temp;
      }
      return img;
    };
    shuffle();
  };

  render() {
    return (
  <div className="p-3 mb-2 bg-light text-dark">
  <div className="row p-3 mb-2 bg-info text-white text-center">
    <div className="col">
      <h4>Clicky Game</h4>
    </div>
    <div className="col">
      <h4>Click an image to begin!</h4>
    </div>
    <div className="col">
      <h4>Score: {this.state.count1} | Top Score: {this.state.count}</h4>
    </div>
  </div>
  <div className="p-3 mb-2 bg-dark text-white">
  <h1 className="text-center">Clicky Game!</h1>
  <h2 className="text-center">Click on an image to earn points, but don't click on any more than once!</h2>
  </div>
  <div className="container">
  {this.state.img.map(imgs => (
        <PicCard
          imageClick={this.imageClick}
          id={imgs.id}
          key={imgs.id}
          image={imgs.image}
          count={this.state.count}
          />
      ))}
  </div>
  <footer className="footer">
  <h2 className="text-center">Try your best to get the highest score!<span>ðŸ¤ª</span></h2>
  </footer> 
  </div>
    );
  }
}

export default App;
